// Role-based order processing
const orderData = $input.first().json;
const { userContext } = orderData;

// Check if user has permission to create orders
const allowedRoles = ['admin', 'vergabe', 'agent'];
const userRole = userContext.role;

if (!allowedRoles.includes(userRole)) {
  return [{
    json: {
      error: 'Insufficient permissions to create orders',
      userRole: userRole,
      success: false
    }
  }];
}

// Generate order ID
const orderId = 'AMP-' + new Date().getFullYear() + '-' + (Math.floor(Math.random() * 10000) + 1000);

// Process order data
const processedOrder = {
  order_id: orderId,
  name: orderData.name,
  address: orderData.address,
  zip: orderData.zip,
  city: orderData.city,
  country: orderData.country,
  phone: orderData.phone,
  branch: orderData.branch,
  description: orderData.description,
  preferred_time: orderData.preferred_time || null,
  comments: orderData.comments || '',
  status: 'offen',
  priority: orderData.priority || 'normal',
  created_by: userContext.telegram_id,
  created_by_role: userRole,
  created_at: new Date().toISOString(),
  assigned_monteur: null,
  updated_at: new Date().toISOString()
};

console.log('Processing order:', orderId, 'by', userRole);

return [{
  json: processedOrder
}];

// Find suitable monteurs based on location and branch
const orderData = $input.first().json;
const { zip, country, branch } = orderData;

// Query parameters for finding monteurs
const zipPrefix = zip.substring(0, 2); // First 2 digits of ZIP

return [{
  json: {
    ...orderData,
    searchZip: zipPrefix,
    searchCountry: country,
    searchBranch: branch
  }
}];

