// Send notifications to admins
const orderData = $input.first().json;
const adminUsers = $input.all().filter(item => item.json.telegram_id);

if (adminUsers.length === 0) {
  return [{
    json: {
      message: 'No admin users found for notification',
      success: false
    }
  }];
}

const sendTasks = adminUsers.map(user => ({
  chatId: user.json.telegram_id,
  text: orderData.notificationMessage,
  orderData: orderData
}));

return sendTasks.map(task => ({ json: task }));
