// AMP Search Logic
const searchData = $input.first().json;
const { criteria, value, role, userContext } = searchData;

// Log search request
console.log(`Search request: ${criteria} = ${value} by ${role}`);

// Search criteria mapping
const searchQueries = {
  plz: { field: 'zip', value: value },
  name: { field: 'name', value: value },
  date: { field: 'created_at', value: value },
  phone: { field: 'phone', value: value },
  order_number: { field: 'order_id', value: value }
};

const searchQuery = searchQueries[criteria];

if (!searchQuery) {
  return [{
    json: {
      error: 'Invalid search criteria',
      success: false
    }
  }];
}

// Role-based search restrictions
let additionalFilters = {};

if (role === 'monteur') {
  // Monteur can only see own orders
  additionalFilters.assigned_monteur = userContext.telegram_id;
}

return [{
  json: {
    searchQuery,
    additionalFilters,
    searchCriteria: criteria,
    searchValue: value,
    userRole: role,
    success: true
  }
}];

// Format search results
const results = $input.all();

if (results.length === 0) {
  return [{
    json: {
      message: 'Keine Ergebnisse gefunden',
      results: [],
      count: 0,
      success: true
    }
  }];
}

// Format results for display
const formattedResults = results.map(item => {
  const data = item.json;
  return {
    id: data.id,
    order_number: data.order_id || data.id,
    customer_name: data.name,
    address: `${data.address}, ${data.zip} ${data.city}`,
    phone: data.phone,
    status: data.status || 'offen',
    created_at: data.created_at,
    assigned_monteur: data.assigned_monteur,
    priority: data.priority || 'normal',
    branch: data.branch,
    country: data.country
  };
});

return [{
  json: {
    message: `${formattedResults.length} Ergebnisse gefunden`,
    results: formattedResults,
    count: formattedResults.length,
    success: true
  }
}];
